}


function getRandChar($length)
{
    $str = null;
    $strPol = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789abcdefghijklmnopqrstuvwxyz";
    $max = strlen($strPol) - 1;

    for ($i = 0;
         $i < $length;
         $i++) {
        $str .= $strPol[rand(0, $max)];
    }

    return $str;
}

/**
 * 创建guid
 * @return string
 */
function guid()
{
    mt_srand((double)microtime() * 10000);
    $charid = strtoupper(md5(uniqid(rand(), true)));
    $hyphen = chr(45);
    $uuid = substr($charid, 0, 8) . $hyphen . substr($charid, 8, 4) . $hyphen . substr($charid, 12, 4) . $hyphen . substr($charid, 16, 4) . $hyphen . substr($charid, 20, 12);
    return $uuid;
}


/**
 * base64转图片
 * @param $base64
 * @return string
 * @throws Exception
 */
function base64toImg($base64)
{
    try {
        // $base64 = '';

        if (empty($base64)) {
            return '';
        }
        $path = dirname($_SERVER['SCRIPT_FILENAME']) . '/static/imgs';
        if (!is_dir($path)) {
            mkdir(iconv("UTF-8", "GBK", $path), 0777, true);
        }
        $img = base64_decode($base64);
        $name = guid();
        $imgUrl = $path . '/' . $name . '.jpg';
        $a = file_put_contents($imgUrl, $img);
        $imgUrl2 = 'static/imgs/' . $name . '.jpg';
        return $a ? $imgUrl2 : '';
    } catch (Exception $e) {
        throw $e;
    }

}


/**
 * 日期加上指定天数
 * @param $count
 * @param $time_old
 * @return false|string
 */
function addDay($count, $time_old)
{
    $time_new = date('Y-m-d', strtotime('+' . $count . ' day',
        strtotime($time_old)));
    return $time_new;

}

/**
 * 日期减去指   定天数
 * @param $count
 * @param $time_old
 * @return false|string
 */
function reduceDay($count, $time_old)
{
    $time_new = date('Y-m-d', strtotime('-' . $count . ' day',
        strtotime($time_old)));
    return $time_new;

}

/**
 * 生成订单号
 * @return string
 */
function makeOrderNo()
{
    $yCode = array('A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J');
    $orderSn =
        $yCode[intval(date('Y')) - 2017] . strtoupper(dechex(date('m'))) . date(
            'd') . substr(time(), -5) . substr(microtime(), 2, 5) . sprintf(
            '%02d', rand(0, 99));
    return $orderSn;
}

